import React, { useState } from 'react'
import { forgotPasswordApi } from '../../apis/Api'
import { toast } from 'react-toastify'

const ForgotPassword = () => {


    //phone number state 
    const [phone, setPhone] = useState('')
    const [isSent, setIsSent] = useState('')

    //send otp function
    const handleForgotPassword = (e) => {
        e.preventDefault()


        //calling api
        forgotPasswordApi({ phone }).then((res) => {
            if (res.status === 200) {
                toast.success(res.data.message)
                setIsSent(true)
            }
        }).catch((error) => {
            if (error.response.status === 400)
                toast.error(error.response.data.message)

        })
    }
    return (
        <>
            <div className='container mt-2'>
                <h3>Forgot Password</h3>
                <form className='w-25'>
                    <span className='d-flex'>
                        <h4>+977</h4>
                        <input disabled={isSent} onChange={(e) => setPhone(e.target.value)} className='form-control' type='number' placeholder='enter yourr valid number'></input>
                    </span>
                    <button disabled={isSent} onClick={handleForgotPassword} className='btn btn-dark w-100 mt-2'>Send OTP</button>



                    {
                        isSent && <>
                        <hr/>

                        <span>OTP has been sent to {phone}âœ… </span>
                        <input type='number' className='from-control mt-2'placeholder='Enter Valid OTP'></input>
                        <input type='text' className='from-control mt-2'placeholder='set New password'></input>
                        <button className='btn btn-primary mt-2 w-100'>verify OTP & set Password</button>



                        </>
                    }

                </form>

            </div>
        </>
    )
}

export default ForgotPassword


