import React, { useState } from 'react'
import { forgotPasswordApi } from '../../apis/Api'
import { toast } from 'react-toastify'

const ForgotPassword = () => {


    //phone number state 
    const [phone,setPhone]=useState('')
    const [isSent,setIsSent]=useState('')

    //send otp function
    const handleForgotPassword=(e)=>{
        e.preventDefult()


        //calling api
        forgotPasswordApi({phone}).then((res)=>{
            if (res.status===200){
                toast.success(res.data.message)
            }
        })
    }
  return (
    <>
    <div className='container mt-2'>
        <h3>Forgot Password</h3>
        <form className='w-25'>
            <span className='d-flex'>
                <h4>+977</h4>
                <input onChange={(e)=>setPhone(e.target.value)} className='form-control' type='number' placeholder='enter yourr valid number'></input>
            </span>
            <button className='btn btn-dark w-100 mt-2'>Send OTP</button>

        </form>

    </div>
    </>
  )
}

export default ForgotPassword


